package edu.unc.ceccr.persistence;

import javax.persistence.*;

@SuppressWarnings("serial")
@Entity
@Table(name = "cbench_descriptors")
public class Descriptors implements java.io.Serializable {
    //For one compound and one descriptor generation program, this class stores all the descriptors.
    //e.g., the string "descriptorValues" could contain the 900 values generated by Dragon for lorazepam.

    private Long descriptorsId;
    private String compoundId; //SMILES string for compound
    private String descriptorValues;
    private String descriptorGeneratorId; //reference to the program that generated these descriptors

    public Descriptors() {
    }

    @Id
    @GeneratedValue(strategy = GenerationType.AUTO)
    @Column(name = "descriptorsId")
    public Long getDescriptorsId() {
        return this.descriptorsId;
    }

    public void setDescriptorsId(Long descriptorsId) {
        this.descriptorsId = descriptorsId;
    }

    @Column(name = "compoundName")
    public String getCompoundId() {
        return this.compoundId;
    }

    public void setCompoundId(String compoundId) {
        this.compoundId = compoundId;
    }

    @Column(name = "descriptorValues")
    public String getDescriptorValues() {
        return this.descriptorValues;
    }

    public void setDescriptorValues(String descriptorValues) {
        this.descriptorValues = descriptorValues;
    }

    @Column(name = "descriptorGeneratorId")
    public String getDescriptorGeneratorId() {
        return this.descriptorGeneratorId;
    }

    public void setDescriptorGeneratorId(String descriptorGeneratorId) {
        this.descriptorGeneratorId = descriptorGeneratorId;
    }

}
